    import React, { useEffect, useState, ChangeEvent, FormEvent } from "react";
    import { Link } from 'react-router-dom';
    import { FiArrowLeft } from 'react-icons/fi';
    import { MapContainer, TileLayer, Marker, Popup,} from 'react-leaflet';
    import axios from 'axios';
    import './styles.css';
    import logo from '../../assetes/logo.svg';
    import api from "../../services/api";

    interface Item {
        id: number;
        title: string;
        image_url: string;
    }

    interface IBGEUFResponse {
        sigla: string;
    }

    interface IBGECityResponse {
        nome: string;
    }

    const CreatePoint = () => {
        const [items, setItems] = useState<Item[]>([]);
        const [ufs, setUfs] = useState<string[]>([]);
        const [cities, setCities] = useState<string[]>([]);

        const [initialPosition, setInitialPosition] = useState<[number, number]>([0,0]);

        const [formData, setFormData] = useState({
            name: '',
            email: '',
            whatsapp: '',
        })

        const [selectUf, setSelectedUf] = useState('0');
        const [selectedCity, setSelectedCity] = useState('0');
        const [selectedItems, setSelectedItems] = useState<number[]>([]);

        useEffect(() => {
            navigator.geolocation.getCurrentPosition(position => {
                const { latitude, longitude } = position.coords;

                setInitialPosition([latitude, longitude]);
            })
        }, [])
        
        useEffect(() => {
            api.get('items').then(response => {
                setItems(response.data);
            })
        }, [])

        useEffect(() => {
            axios.get<IBGEUFResponse[]>('https://servicodados.ibge.gov.br/api/v1/localidades/estados').then(response => {
                const ufInitials = response.data.map(uf => uf.sigla);

                setUfs(ufInitials);
            });
        }, []);

        useEffect(() => {
           if (selectUf === '0') {
            return;
           } 

           axios.get<IBGECityResponse[]>(`https://servicodados.ibge.gov.br/api/v1/localidades/estados/${selectUf}/municipios`).then(response => {
                const cityNames = response.data.map(city => city.nome);

                setCities(cityNames);
            });

        }, [selectUf])

        function handleSelectUf(event: ChangeEvent<HTMLSelectElement>) {
            const uf = event.target.value;

            setSelectedUf(uf);
        }

        function handleSelectCity(event: ChangeEvent<HTMLSelectElement>) {
            const city = event.target.value;

            setSelectedCity(city);
        }

        function handleInputChange(event: ChangeEvent<HTMLInputElement>) {
            const { name, value } = event.target;

            setFormData({ ...formData, [name]: value })
        }

        function handleSelectedItem(id: number) {
            const alreadySelected = selectedItems.findIndex(item => item === id );

            if (alreadySelected >= 0) {
                const filteredItems = selectedItems.filter(item => item !== id);

                setSelectedItems(filteredItems);
            }
            else {
                setSelectedItems([...selectedItems, id ]);
            }

            
        }

        async function handleSubmit(event: FormEvent) {
            event.preventDefault();

            const { name, email, whatsapp } = formData;
            const uf = selectUf;
            const city = selectedCity;
            const items = selectedItems;
            const latitude = -21.599746;
            const longitude =  -48.8370901;

            const data = {
                name,
                email,
                whatsapp,
                uf,
                city,
                latitude,
                longitude,
                items
            }

            await api.post('points', data);

            alert('Ponto de Coleta Criado com Sucesso!!');
        }

        return (
            <div id="page-create-point">
                <header>
                    <img src={logo} alt="Ecoleta" />
                    <Link to="/">
                        <FiArrowLeft />
                        Voltar para Home
                    </Link>
                </header>
                <form onSubmit={handleSubmit}>
                    <h1>Cadastro do <br /> Ponto de coleta</h1>

                    <fieldset>
                        <legend>
                            <h2>Dados</h2>
                        </legend>
                        <div className="field">
                            <label htmlFor="name">Nome de entidade</label>
                            <input 
                                type="text"
                                name="name"
                                id="name"
                                onChange={handleInputChange}
                            />
                        </div>
                        <div className="field-group">
                            <div className="field">
                                <label htmlFor="email">E-mail</label>
                                <input 
                                    type="email"
                                    name="email"
                                    id="email"
                                    onChange={handleInputChange}
                                />
                            </div>

                            <div className="field">
                            <label htmlFor="whatsapp">Whatsapp</label>
                            <input 
                                type="text"
                                name="whatsapp"
                                id="whatsapp"
                                onChange={handleInputChange}
                            />
                        </div>
                        </div>
                    </fieldset>


                    <fieldset>
                        <legend>
                            <h2>Endereço</h2>
                            <span>Selecione o endereço no mapa</span>
                        </legend>
                        
                        <div className="field-group">
                            <div className="field">
                                <label htmlFor="uf">Estado (UF)</label>
                                <select 
                                    name="uf" 
                                    id="uf" 
                                    value={selectUf} 
                                    onChange={handleSelectUf} 
                                >
                                    <option value="0">Seleciona uma UF</option>
                                    {ufs.map(uf => (
                                        <option key={uf} value={uf}>{uf}</option>    
                                    ))}
                                </select>
                            </div>
                            <div className="field">
                                <label htmlFor="city">cidade</label>
                                <select 
                                    name="city" 
                                    id="city" 
                                    value={selectedCity} 
                                    onChange={handleSelectCity}
                                >
                                    <option value="0">Seleciona uma Cidade</option>
                                    {cities.map(city => (
                                        <option key={city} value={city}>{city}</option>    
                                    ))}
                                </select>
                            </div>
                        </div>
                    </fieldset>

                    <MapContainer center={initialPosition} zoom={13} scrollWheelZoom={false} >
                        <TileLayer
                        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                        />
                        
                        <Marker position={[ -21.599746, -48.8370901 ]} >
                            <Popup>
                                A pretty CSS3 popup. <br /> Easily customizable.
                            </Popup>
                        </Marker>
                        
                    </MapContainer>
                    
                    <fieldset>
                        <legend>
                            <h2>Ítens de Coleta</h2>
                            <span>Seleciona um ou mais ítens abaixo</span>
                        </legend>
                        <ul className="items-grid">
                            {items.map(item => (
                            <li 
                                key={item.id} 
                                onClick={() => handleSelectedItem(item.id)}
                                className={selectedItems.includes(item.id) ? 'selected' : '' }                            >
                                <img src={item.image_url} alt={item.title} />
                                <span>{item.title}</span>
                            </li>
                            ))}

                        </ul>
                    </fieldset>

                    <button type="submit">
                        Cadastrar Ponto de Coleta
                    </button>
                </form>
            </div>
        );
    };

    export default CreatePoint;
